mapper.sql:
WHERE br.deleted = 0

    AND br.record_date BETWEEN #{dto.recordStartDate} AND #{dto.recordEndDate}


    AND br.record_source like concat('%', #{dto.recordSource}, '%')


    AND br.record_type = #{dto.recordType}


    AND br.is_statistics = 1


    AND br.amount >=  #{dto.mixAmount}


    AND br.amount <=  #{dto.maxAmount}


    AND bdbr.dict_id in #{tagId}

mapper.select:
SELECT SUM(amount) AS totalAmount, count(*) AS totalRecordNum
FROM bookkeeping_records
WHERE deleted = 0
AND record_date BETWEEN #{dto.currentStartMonth} AND #{dto.currentEndMonth}

    AND record_category = #{dto.recordCategory}


    AND is_statistics = 1

SELECT record_type, sum(amount) AS amount, count(*) AS recordNum
FROM bookkeeping_records
WHERE deleted = 0
AND record_category = #{dto.recordCategory}
AND record_date BETWEEN #{dto.currentStartMonth} AND #{dto.currentEndMonth}

    AND is_statistics = 1


    AND record_type in #{item}

GROUP BY record_type
ORDER BY amount desc
